#Combien y-a-t'il de handle contenant 5 caractères (le @ ne compte pas pour un caractère)


def five_characters(handle_twitter)
    puts " Combien y-a-t-il de handles contenant 5 caractères (excluant le @) ?"

    count = 0 

    handle_twitter.each do |handle|

        if (handle.length - 1) == 5
            count += 1
        else
        end
    end
    puts " Le nombre de handles de 5 caractères est : #{count}"
    return count
end


#Combien commencent par une majuscule (première lettre juste après le @) ?
def capital_letter
    puts "Combien d'identifiant commencent par une majuscule?"
    count = 0

    count = handle_twitter.count { |s| s.match? /\A@[[:upper:]]/ }
    puts " Il y en a : #{count}"

    return count
end
#Trie la liste de handle par ordre alphabétique.
def alphabetical_list
    puts "Liste triée par ordre alphabétique"
    handle_twitter
    sorted_list_alpha = []
    sorted_list_alpha = handle_twitter

    sorted_list_alpha.each do |handle|
        puts "#{handle}"
        
    end
    return sorted_list_alpha
end


#Trie la liste de handle par taille des handle (les plus petits en premiers, les plus grands après)
def sort_size(handle_twitter)
    puts " La liste triée par taille de handle est la suivante : "
    sorted_list_size = []
    sorted_list_size = handle_twitter.sort_by {|x| x.length}

    sorted_list_size.each do |handle|
        puts " #{handle} "

#    arr = arr.sort_by {|x| x.length}
    end

    return sorted_list_size
end

#Quelle est la position dans l'array de la personne @epenser ?
def epenser
    epenser_position = handle_twitter.index(@epenser)
    puts "@epenser se trouve en position : #{epenser_position} dans l'array, ce qui signifie que c'est le #{epenser_position + 1}ème"
    return epenser_position
end

